import React from 'react'
import { fontSizer, getCols, } from '../utils/utils'

class Products extends React.PureComponent {
    state = {
        isBottom: false
    }

    render = () => {
        const products = [
            {
                image: '',
                title: 'Task Light',
                price: '600.00'
            },
            {
                image: '',
                title: 'Task Light',
                price: '600.00'
            },
            {
                image: '',
                title: 'Dresser Light',
                price: '900.00'
            },
            {
                image: '',
                title: 'Puzzle Tea Light',
                price: '120.00'
            },
        ]

        return (
            <div className="container">
                <div className="sticky-text">
                    { !this.state.isBottom ? (
                        <p>
                            Our objects are completely stripped down, they have nothing to hide. Good looking and good for the world.
                            <strong>→ More about the studio</strong>
                        </p>
                    ) : (
                        <div className="instagram-post" />
                    )}
                </div>

                { this.renderProducts(products) } 

                <style jsx>{`
                    .container {
                        position: relative;
                        display: flex;
                        flex-direction: column;
                        align-items: flex-end;

                        width: 100%;
                        height: 100%;

                        padding: ${ getCols(1) }vw;
                    }

                    .sticky-text {
                        position: absolute;
                        top: ${getCols(1)}vw;
                        left: ${ getCols(1) }vw;

                        font-size: ${ fontSizer(20) }vw;
                        line-height: 1.3em;
                        letters-spacing: -.01em;

                        width: ${ getCols(7) }vw;
                    }

                    .sticky-text strong {
                        display: block;
                        margin-top: .8em;
                    }
                `}</style>
            </div>
        )
    }

    renderProducts = products => {
        return products.map(product => {
            return (
                <div className="product-card">
                    <div className="product-image" />

                    <div className="caption">
                        <strong>{ product.title }</strong>
                        EUR { product.price }
                    </div>

                    <style jsx>{`
                        .product-card {
                            display: flex;
                            flex-direction: column;

                            width: ${ getCols(13) }vw;
                        }

                        .product-image {
                            width: 100%;
                            height: ${ getCols(13*1.2) }vw;
                            background: #F7F7F7;
                        }

                        .caption {
                            margin-top: 1em;
                        }

                        .caption strong {
                            display: block;
                        }
                    `}</style>
                </div>
            )
        })
    }
}

export default Products
