import React from 'react'
import delay from 'delay'

import { fontSizer, getCols, } from '../utils/utils'

class Products extends React.PureComponent {
    state = {
        position: 'initial'
    }

    wrapperBounds = false

    async componentDidMount() {
        await delay(300)
        this.props.rafLoop && this.props.rafLoop.add(this.onScroll)
    }

    render = () => {
        const products = [
            {
                image: '/static/images/dresser-light.png',
                title: 'Task Light',
                price: '600.00'
            },
            {
                image: '/static/images/tea-light.png',
                title: 'Task Light',
                price: '600.00'
            },
            {
                image: '/static/images/dresser-light.png',
                title: 'Dresser Light',
                price: '900.00'
            },
            {
                image: '/static/images/tea-light.png',
                title: 'Puzzle Tea Light',
                price: '120.00'
            },
        ]

        return (
            <div className="container" ref={ el => this.stickyWrapper = el }>
                <div className={`sticky-text ${ this.state.position }`} ref={ el => this.stickyEl = el }>
                    <p>
                        Our objects are completely stripped down, they have nothing to hide. Good looking and good for the world.
                        <strong> More about the studio</strong>
                    </p>
                </div>

                { this.state.position === 'bottom' && this.renderInstagramPost() }

                <div className="products">
                    { this.renderProducts(products) } 
                </div>

                { typeof window !== 'undefined' && window.innerWidth < 1024  && this.renderInstagramPost() } 

                <style jsx>{`
                    .container {
                        position: relative;
                        display: flex;
                        flex-direction: column;

                        width: 100%;
                        height: 100%;

                        padding: ${ getCols(2) }vw;
                    }

                    .sticky-text {
                        font-size: 16px; // TODO: responsive font size mobile
                        line-height: 1.3em;
                        letter-spacing: -.01em;

                        width: 90%;
                        margin-bottom: ${ getCols(4.85) }vw;
                        opacity: ${ this.state.position === 'bottom' ? 0 : 1 };
                    }

                    .sticky-text strong {
                        display: block;
                        margin-top: .8em;
                    }

                    @media (min-width: 900px) { 
                        .container {
                            align-items: flex-end;

                            padding: ${ getCols(1) }vw;
                        }

                        .sticky-text {
                            position: absolute;
                            top: ${getCols(1)}vw;
                            left: ${ getCols(1) }vw;

                            font-size: ${ fontSizer(20) }vw;
                            line-height: 1.3em;
                            letter-spacing: -.01em;

                            width: ${ getCols(7) }vw;
                            margin-bottom: 0;
                        }

                        .sticky-text.fixed {
                            position: fixed;
                        }
                    }
                `}</style>
            </div>
        )
    }

    renderProducts = products => {
        return products.map((product, i) => {
            return (
                <div className="product-card" key={i}>
                    <div className="product-image" />

                    <div className="caption">
                        <strong>{ product.title }</strong>
                        EUR { product.price }
                    </div>

                    <style jsx>{`
                        .product-card {
                            display: flex;
                            flex-direction: column;

                            width: 100%;

                            margin-bottom: ${ getCols(2.5) }vw;
                        }

                        .product-card:last-of-type {
                            margin-bottom: ${ getCols(6) }vw;
                        }

                        .product-image {
                            width: 100%;
                            height: ${ getCols(26*1.2) }vw;
                            background: #F7F7F7;

                            background-image: url('${ product.image }');
                            background-position: center;
                            background-repeat: no-repeat;
                            background-size: contain;
                        }

                        .caption {
                            font-size: 14px; // TODO: responsive font size mobile
                            line-height: 1.43em;

                            margin: 1.5em 0;
                        }

                        .caption strong {
                            display: block;
                        }

                        @media (min-width: 900px) { 
                            .product-card {
                                width: ${ getCols(13) }vw;

                                margin-bottom: ${ getCols(1.25) }vw;
                            }

                            .product-card:last-of-type {
                                margin-bottom: ${ getCols(.3) }vw;
                            }

                            .product-image {
                                height: ${ getCols(13*1.2) }vw;
                            }

                            .caption {
                                font-size: ${ fontSizer(16) }vw;
                                line-height: 1.37em;

                                margin: 1.75em 0;
                            }
                        }
                    `}</style>
                </div>
            )
        })
    }

    renderInstagramPost = () => {
        return (
            <div className="instagram-post">
                <div className="instagram-image">
                </div>

                <div className="caption">
                    <strong>Stay posted</strong>
                    Follow us on instagram
                </div>

                <style jsx>{`
                    .instagram-post {
                        display: flex;
                        flex-direction: column;
                        align-items: flex-end;

                        width: 100%;
                    }

                    .instagram-image {
                        width: ${ getCols(13) }vw;
                        height: ${ getCols(13 * 1.25) }vw;

                        background-image: url('/static/images/instagram.jpg');
                        background-size: cover;
                        background-repeat: no-repeat;
                    }

                    .caption {
                        font-size: 14px; // TODO: responsive font size mobile
                        line-height: 1.43em;

                        margin: 1.5em 0;

                        width: ${ getCols(13) }vw;
                    }

                    .caption strong {
                        display: block;
                    }

                    @media (min-width: 900px) { 
                        .instagram-post {

                        }

                        .caption {
                            font-size: ${ fontSizer(16) }vw;
                            line-height: 1.37em;

                            margin: 1.75em 0;
                        }
                    }
                `}</style>
            </div>
        )
    }

    onScroll = args => {
        if( window.innerWidth < 1024 ) return this.props.rafLoop.remove(this.onScroll)
        if( !this.stickyEl || !this.stickyWrapper ) return

        if( !this.wrapperBounds ) 
            this.wrapperBounds = this.stickyWrapper.getBoundingClientRect()

        const {top = 0, bottom = 0} = this.wrapperBounds

        let position = 'initial'

        console.log(args.scrollY, top)
        if( args.scrollY > top ) {
            position = 'fixed'
        } else if (args.scrollY + window.innerHeight >= bottom) {
            position = 'bottom'
        }

        if( position === this.state.position ) return

        this.setState({
            position: position
        })
    }
}

export default Products
