import React from 'react'
import { fontSizer, getCols, } from '../utils/utils'

class Products extends React.PureComponent {
    state = {
        isBottom: false
    }

    render = () => {
        const products = [
            {
                image: '/static/images/dresser-light.png',
                title: 'Task Light',
                price: '600.00'
            },
            {
                image: '/static/images/tea-light.png',
                title: 'Task Light',
                price: '600.00'
            },
            {
                image: '/static/images/dresser-light.png',
                title: 'Dresser Light',
                price: '900.00'
            },
            {
                image: '/static/images/tea-light.png',
                title: 'Puzzle Tea Light',
                price: '120.00'
            },
        ]

        return (
            <div className="container">
                <div className="sticky-text">
                    { !this.state.isBottom ? (
                        <p>
                            Our objects are completely stripped down, they have nothing to hide. Good looking and good for the world.
                            <strong>→ More about the studio</strong>
                        </p>
                    ) : (
                        <div className="instagram-post" />
                    )}
                </div>

                { this.renderProducts(products) } 

                <style jsx>{`
                    .container {
                        position: relative;
                        display: flex;
                        flex-direction: column;

                        width: 100%;
                        height: 100%;

                        padding: ${ getCols(2) }vw;
                    }

                    .sticky-text {
                        font-size: 16px; // TODO: responsive font size mobile
                        line-height: 1.3em;
                        letter-spacing: -.01em;

                        width: 90%;
                        margin-bottom: ${ getCols(4.85) }vw;
                    }

                    .sticky-text strong {
                        display: block;
                        margin-top: .8em;
                    }

                    @media (min-width: 900px) { 
                        .container {
                            align-items: flex-end;

                            padding: ${ getCols(1) }vw;
                        }

                        .sticky-text {
                            position: absolute;
                            top: ${getCols(1)}vw;
                            left: ${ getCols(1) }vw;

                            font-size: ${ fontSizer(20) }vw;
                            line-height: 1.3em;
                            letter-spacing: -.01em;

                            width: ${ getCols(7) }vw;
                            margin-bottom: 0;
                        }
                    }
                `}</style>
            </div>
        )
    }

    renderProducts = products => {
        return products.map(product => {
            return (
                <div className="product-card">
                    <div className="product-image" />

                    <div className="caption">
                        <strong>{ product.title }</strong>
                        EUR { product.price }
                    </div>

                    <style jsx>{`
                        .product-card {
                            display: flex;
                            flex-direction: column;

                            width: 100%;

                            margin-bottom: ${ getCols(1) }vw;
                        }

                        .product-card:last-of-type {
                            margin-bottom: ${ getCols(.3) }vw;
                        }

                        .product-image {
                            width: 100%;
                            height: ${ getCols(26*1.2) }vw;
                            background: #F7F7F7;

                            background-image: url('${ product.image }');
                            background-position: center;
                            background-repeat: no-repeat;
                            background-size: contain;
                        }

                        .caption {
                            font-size: 14px; // TODO: responsive font size mobile
                            line-height: 1.43em;

                            margin: 1.5em 0;
                        }

                        .caption strong {
                            display: block;
                        }

                        @media (min-width: 900px) { 
                            .product-card {
                                width: ${ getCols(13) }vw;

                                margin-bottom: ${ getCols(1.25) }vw;
                            }

                            .product-image {
                                height: ${ getCols(13*1.2) }vw;
                            }

                            .caption {
                                font-size: ${ fontSizer(16) }vw;
                                line-height: 1.37em;

                                margin: 1.75em 0;
                            }
                        }
                    `}</style>
                </div>
            )
        })
    }
}

export default Products
